function isDefined(name){return typeof name !== 'undefined';};String.prototype.toCamelCase = function(){return this.replace(/(\_[a-z])/g,function($1){return $1.toUpperCase().replace('_','');});};String.prototype.toSnakeCase = function(){return this.replace(/([A-Z])/g,function($1){return "_"+$1.toLowerCase();});};
